import React, { useState } from 'react';
import TodoForm from './TodoForm';
import { RiCloseCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';

const TodoList = ({ todos, completeTodo, removeTodo, updateTodo }) => {
  const [edit, setEdit] = useState({
    id: null,
    value: ''
  });

  const submitUpdate = value => {
    updateTodo(edit.id, value);
    setEdit({
      id: null,
      value: ''
    });
  };

  const demo = () => {
    {edit !== 0 ? <TodoForm edit={edit} onSubmit={submitUpdate} /> : ''}
  }

  return todos.map((todo, index) => (
    <div
      className={todo.isComplete ? 'complete' : 'todo-row'}
      key={index}
    >
      <div>
        <input className='checkbox' type='checkbox' key={todo.id} onClick={() => completeTodo(todo.id)} />
           {todo.text}
      </div>
      <div className='icons'>
        <RiCloseCircleLine
          onClick={() => removeTodo(todo.id)}
          className='delete-icon'
        />
        <TiEdit
          onClick={demo}
            // setEdit({ id: todo.id, value: todo.text })}
          className='edit-icon'
        />
      </div>
    </div>
  ));
};

export default TodoList;
